function sidebarFunctions(){if(!document.querySelector(".sidebar"))return null;document.querySelectorAll("[data-menu-item]").forEach((e=>{const t=e.querySelector("[data-menu-item-arrow]");t&&t.addEventListener("click",(()=>{e.classList.toggle("show")}))}));let e=document.querySelectorAll(".sidebar__menu-link, .sidebar__menu-sublink");e.forEach((t=>{t.addEventListener("click",(r=>{r.target.classList.contains("sidebar__menu-arrow")||(e.forEach((e=>{e.classList.remove("active")})),t.classList.add("active"))}))}))}sidebarFunctions();